const ProductContext = React.createContext()

function  ContextProvider(props) {




// FOR CHANGING BLOCK AND DISTRICT FROM LOGIN SCREEN

    const [block, setblock] = useState("0")
    const [district, setdistrict] = useState("0")

// FOR CHANGING BLOCK AND DISTRICT FROM LOGIN SCREEN




    const [values,setValues] = useState({
        // cart : [],
        products_veg: [],
        _home_screen : 'kit', // kit or veg
        kit_screen : '200',      //200 or 400
        // user_address : [],
    })

    const[userValues,setUserValues] = useState({
        user_address : ["sree","abcd"],
   })


   const[Cart,setCart] = useState({
        cart : [],
    })


    const initUserAddress = async ()=>{
        let phoneNo = isAuthenticated().phoneNumber;
        let phoneNumber ;

        console.log("init address")

        try
        {
            phoneNumber = (phoneNo).substring(3);
        }catch{

        }

        await axios({
            'method'    :   'GET',
            'url'       :   `${Base_URL}/user/${phoneNumber}`,
            'headers'   :   {
                                'content-type': 'application/x-www-form-urlencoded;charset=utf-8',
                            },
            'data'      :  qs.stringify({
                            }),
        })
        .then (res =>{
            // if success
            if(res.status === 201)
            {
                // console.log(res.data)
                if( (res.data.full_name==='notfound') )
                {
                    // console.log("no user");
                    UpdateNullAddress() ;
                }
                else
                {
                    setUserValues({
                                    ...userValues,
                                    user_address : [res.data], // match it from backend
                                });    
                }

            }
            else {
                console.log("401 : " , res.data )
            }
        }).catch(err=>{
        })



    }




    const UpdateNullAddress = async ()=>{

        let phoneNo = isAuthenticated().phoneNumber;

        console.log(" updateNull address : " , district , block );

        let phoneNumber;
        try{
            phoneNumber = (phoneNo).substring(3);
        }
        catch{

        }

        await axios({
            'method'    :   'POST',
            'url'       :   `${Base_URL}/user/`,
            'headers'   :   {
                                'content-type': 'application/json;charset=utf-8',
                            },
            'data'      :  {
                                id:`${phoneNumber}`,
                                full_name:"name",
                                house_name:"name",
                                street_name:"name", 
                                pincode:"name",
                                district:district, 
                                block:block,
                           },
        })
        .then(res =>{
            // if success
            if(res.status === 201)
            {
                console.log(" data pushed : " , res.data )

                // let phoneNo = isAuthenticated().phoneNumber;
                // let phoneNumber;

                setUserValues({
                                ...userValues,
                                user_address : [res.data], // match it from backend
                            })
            }
        }).catch(err=>{

        })

    }






const initKitProducts = async ()=>{

    // console.log("init kit ");
    // await fetch('https://api.npms.io/v2/search?q=react')
    await axios({
        'method'    :   'GET',
        'url'       :   `${Base_URL}/products/`,
        'headers'   :   {
                            'content-type': 'application/x-www-form-urlencoded;charset=utf-8',
                        },
        'data'      :  qs.stringify({
                        // 'userPhone': phoneNumber,
                        }),
    })
    .then(res =>{
        // if success
        // console.log("da njan vanneyy : ", res.status );
        if(res.status === 201)
        {
            setValues({
                ...values,
                products_veg : res.data, // match it from backend
            })
            // console.log(res.data);
        }
    }).catch(err=>{
    })

}




   useEffect( async ()=>{
        // call function to initialize data to product kit
        if(isAuthenticated())
        {
            await initUserAddress()
        }


        await initKitProducts(  )
        // await initCartItems()

        // await initOrderPlaced()

    },[])




    return (
        <ProductContext.Provider value={{
            ...userValues,
            ...values,
            block,
            district,

            setValues : setValues,
            setUserValues : setUserValues,


            setblock:setblock,
            setdistrict:setdistrict,

            initUserAddress:initUserAddress,

        }}>
            {props.children}
        </ProductContext.Provider>
    )
}


