import styled from 'styled-components'
import { Link } from 'react-router-dom'


/*================= Footer Container  ==================*/

export const FooterContainer = styled.footer`
    background-color: #010606;


`

/*================= Footer Container -> Footer Wrapper ==================*/

export const FooterWrap = styled.div`
    padding: 48px 24px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    max-width: 1100px;
    margin: 0 auto;

`

/*================= Footer Container -> Footer Wrapper -> Footer Links Container  ==================*/

export const FooterLinksContainer = styled.div`
    display: flex;
    justify-content: center;


    @media screen and (max-width: 820px){
        padding-top: 32px;
    }
`

/*================= Footer Container -> Footer Wrapper -> Footer Links Container -> Footer Links Wrapper ==================*/

export const FooterLinksWrapper = styled.div`
    display: flex;

    @media screen and (max-width: 820px){
        flex-direction: column;
    }
`

/*================= Footer Container -> Footer Wrapper -> Footer Links Container -> Footer Links Wrapper -> Footer Link Items ==================*/

export const FooterLinkItems = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    margin: 16px;
    text-align: left;
    width: 160px;
    box-sizing: border-box;
    color: #fff;

    @media screen and (max-width: 420px){
        margin: 0;
        padding: 10px;
        width: 100%;      
    }

`

/*================= Footer Container -> Footer Wrapper -> Footer Links Container -> Footer Links Wrapper -> Footer Link Items  -> Footer Link Title ==================*/

export const FooterLinkTitle = styled.h1`
    font-size: 16px;
    margin-bottom: 16px;
`

/*================= Footer Container -> Footer Wrapper -> Footer Links Container -> Footer Links Wrapper -> Footer Link Items  -> Footer Link Title  -> Links ==================*/

export const FooterLink = styled(Link)`
    color: #fff;
    text-decoration: none;
    margin-bottom: 0.5rem;
    font-size: 14px;
    transition: 0.3s ease-in-out;

    &:hover {
        color: #01bf71;
        transition: 0.3s ease-in-out;
    }
`

/*================= Footer Container -> Footer Wrapper -> Social Media Handles ==================*/

export const SocialMediaContainer = styled.section`
    max-width: 1000px;
    width: 100%;

`

export const SocialMediaWrap = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1100px;
    margin: 40px auto 0 auto ;

    @media screen and (max-width: 820px){
        flex-direction: column;

    }
`

export const NaadanvipaniLogo = styled(Link)`
    color: #fff;
    justify-self: start;
    cursor: pointer;
    text-decoration: none;
    font-size: 1.5rem;
    display: flex;
    align-items: center;
    margin-bottom: 16px;
    font-weight: 800;
    transition: all 0.3s ease-in-out;

    &:hover{
        font-size: 1.6rem;
        transition: all 0.3s ease-in-out;
    }

` 

export const WebsiteRights = styled.small`
    color: #fff;
    margin-bottom: 16px;
    cursor: default;
`

export const SocialMediaIcons = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 240px;

`

export const SocialMediaIconLink = styled.a`
    color: #fff;
    font-size: 24px;
    transition: 0.3s ease-in-out;

    &:hover{
        color: #01bf71;
        transition: 0.3s ease-in-out;
    }
`
