import React, { useState, useEffect, useContext } from 'react';
import "./style.css";
import Select from 'react-select';

import {ContextConsumer} from '../../utils/Context'


const AddressElement = () => {

      const contextValues = useContext(ContextConsumer);

      // history.push("/address");

      if( contextValues.user_address[0] )
      {
            console.log("user : " , contextValues.user_address[0].full_name )
            if(contextValues.user_address[0].full_name==="name")
            {

            }
      }


      // useEffect(() => {
      //   let isMounted = true;
      //   someAsyncOperation().then(data => {
      //     if (isMounted) setState(data);
      //   })
      //   return () => { isMounted = false }; 
      // }, []);


      const [inputs, setInputs] = useState({
        full_name: "",
        house_name: "",
        pincode: "",
        street_name: ""
      });

      const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
      }




      const handleSubmit = (event) => {
        event.preventDefault();
        alert(inputs);
      }





    return (




  <div class="container-fluid">
  <div class="row">


  <div class="addressbox col-lg-6 col-md-12 col-sm-12 col-xm-12"> 


    <form>


        <div class="heading">      
          <h2>Enter your Address</h2>
        </div>

        <div class="group col-lg-12 col-md-12 col-sm-12 col-xm-12">      
          <input type="text" placeholder="full_name"  name="full_name" 
            value={inputs.full_name || ""} 
            onChange={handleChange}
            required/>

        </div>

        <div class="group col-lg-12 col-md-12 col-sm-12 col-xm-12">      
          <input type="text" placeholder="house_name"  name="house_name" 
            value={inputs.house_name || ""} 
            onChange={handleChange}
            required/>

        </div>

        <div class="group col-lg-12 col-md-12 col-sm-12 col-xm-12">      

          <input type="text" placeholder="street_name"  name="street_name" 
              value={inputs.street_name || ""} 
              onChange={handleChange}
              required/>

        </div>

        <div class="group col-lg-12 col-md-12 col-sm-12 col-xm-12">      
          <input type="text" placeholder="pincode"  name="pincode" 
              value={inputs.pincode || ""} 
              onChange={handleChange}
              required/>

        </div>

        <div class="group col-lg-12 col-md-12 col-sm-12 col-xm-12">      

          <select name="District" id="place" class="select"
              value={inputs.pincode || ""} 
              onChange={handleChange}
            >
            <option value="Trivandrum">Trivandrum</option>
            <option value="saab">Saab</option>
            <option value="opel">Opel</option>
            <option value="audi">Audi</option>
          </select>
        </div>

        <div class="group col-lg-12 col-md-12 col-sm-12 col-xm-12 col-12"> 

        <select name="District" id="place" class="select">
          <option value="volvo">Volvo</option>
          <option value="saab">Saab</option>
          <option value="opel">Opel</option>
          <option value="audi">Audi</option>
        </select>
  </div>

  <div class="group col-lg-12 col-md-12 col-sm-12 col-xm-12"> 
    <p>Delivery Time Slot</p>
      <div class="radio-box flex col-lg-6 col-md-6 col-sm-6 col-xm-6">
        <input type="radio" name="radio" id="radio1" value="M"/>
        <label for="radio1">Morning Section-9am to 1pm </label>
      </div>

    <div class="radio-box flex col-lg-6 col-md-6 col-sm-6 col-xm-6">
      <input type="radio" name="radio" id="radio2" value="A"/>
      <label for="radio2">Afternon Section-1Pm to 6pm</label>
    </div>
  </div>

  {/* <input type="submit" /> */}




  </form>


      <button className="continue_btn" onClick={()=>{ console.log(inputs) }} >
      Continue
        </button>

        </div>
        <div className="price-total col-lg-12 col-md-12 col-sm-12 col-xm-12">
          <h4>Total Price : 1000/-</h4>
        </div>

  </div>
</div>

    )
}

export default AddressElement;
