import CounterElement from "../CounterElement";


import {ContextConsumer} from '../../utils/Context'

const Products = ({heading, data}) => {

    const contextValues = useContext(ContextConsumer);

    // console.log("ContextConsumer: ", values);

    // useEffect(  () =>{
