                            {/* } */}

                            {/* <ProductPriceWrapper>
                                <ProductNewPrice>₹{product.newPrice}</ProductNewPrice>
                                <ProductOldPrice>₹{product.oldPrice}</ProductOldPrice>
                            </ProductPriceWrapper> */}
                            <ProductBtnWrapper>

                                <AddToBagBtn onClick={()=>{ contextValues.AddToCart(product.id , 1) }}  to='/' >
                                Buy { <BagIcon /> }
                                    </AddToBagBtn>

                                </ProductBtnWrapper>
                        </ProductDetailsWrapper>
                        </ProductCard>
                    )

                })}

            </ProductsWrapper>
        </ProductsContainer>
    )
}

export default Products
