const Base_URL = 'http://localhost:4000'







const ProductContext = React.createContext()

function  ContextProvider(props) {

    const [values,setValues] = useState({
        // cart : [],
        products_veg: [],
        _home_screen : 'kit', // kit or veg
        kit_screen : '200',      //200 or 400
        // user_address : [],

    })

    const[userValues,setUserValues] = useState({
        user_address : ["sree","abcd"],
   })


   const[Cart,setCart] = useState({
        cart : ["sree","abcd"],
    })


    const initUserAddress = async ()=>{


        let phoneNo = isAuthenticated().phoneNumber;
        // console.log("phone num init user:" , phoneNo);
        let phoneNumber ;
        try
        {
            phoneNumber = (phoneNo).substring(3);
            // console.log("phone num rem init user :" , phoneNumber);
        }
        catch{

        }

        await axios({
            'method'    :   'GET',
            'url'       :   `${Base_URL}/user/11`,
            'headers'   :   {
                                'content-type': 'application/x-www-form-urlencoded;charset=utf-8',
                            },
            'data'      :  qs.stringify({
                            // 'userPhone': phoneNumber,
                            // 'userPhone': phoneNumber,

                            }),
        })
        .then (res =>{
            // if success
            if(res.status === 201)
            {

                if( (res.data.full_name==='notfound') )
                {

                    console.log("no user");
                    setUserValues({
                        ...userValues,
                        user_address :[],
                    });

                    UpdateNullAddress() ;
                    // console.log("here at update null");

                }
                else
                {
                    console.log("initUserAddress :", res.data);

                    // setUserValues({
                    //                 ...userValues,
                    //                 // products_veg : fakedata.products, // match it from backend
                    //                 user_address : [res.data.useraddress[0]], // match it from backend
                    //             });

                }

                // console.log("data : ",userValues);
            }
            else {
                console.log("401 : " , res.data )
            }
        }).catch(err=>{
            // console.log("err : " , err )
            // setting dummy data 
        })



    }








    const UpdateNullAddress = async ()=>{

        let phoneNo = isAuthenticated().phoneNumber;

        let phoneNumber;
        try{
            phoneNumber = (phoneNo).substring(3);
        }
        catch{

        }

        await axios({
            'method'    :   'POST',
            'url'       :   `${Base_URL}/user/`,
            'headers'   :   {
                                'content-type': 'application/json;charset=utf-8',
                            },
            'data'      :  {
                                id:"11",
                                full_name:"name",
                                house_name:"name",
                                street_name:"name", 
                                pincode:"name",
                                district:1, 
                                block:1
                           },
        })
        .then(res =>{
            // if success
            if(res.status === 201)
            {
                console.log(" data pushed : " , res.data )
                // let phoneNo = isAuthenticated().phoneNumber;
                // let phoneNumber;

                setUserValues({
                                ...userValues,
                                // user_address:[{ name:"",address:"", house_name:"",landmark:"",pin_or_zip:"",town_or_city:"",phone:phoneNumber }], // match it from backend
                                user_address :[],
                            })
            }
        }).catch(err=>{

        })

    }






   const initKitProducts = async ()=>{

    // console.log("init kit ");
    // await fetch('https://api.npms.io/v2/search?q=react')
    await axios({
        'method'    :   'GET',
        'url'       :   `${Base_URL}/products/`,
        'headers'   :   {
                            'content-type': 'application/x-www-form-urlencoded;charset=utf-8',
                        },
        'data'      :  qs.stringify({
                        // 'userPhone': phoneNumber,


                        }),
    })
    .then(res =>{
        // if success
        // console.log("da njan vanneyy : ", res.status );
        if(res.status === 201)
        {
            setValues({
                ...values,
                products_veg : res.data, // match it from backend
            })
            // console.log(res.data);
        }
    }).catch(err=>{
        // setting dummy data
        // delete this on production
        // setValues({
        //     ...values,
        //     products_veg : fakedata.products 
        // })
    })

}




   useEffect( async ()=>{
        // call function to initialize data to product kit
        await initUserAddress()
        await initKitProducts()
        // await initCartItems()

        // await initOrderPlaced()

    },[])




    return (
        <ProductContext.Provider value={{
            ...userValues,
            ...values,
            setValues : setValues,
            setUserValues : setUserValues,
        }}>
            {props.children}
        </ProductContext.Provider>
    )
}


